mapper.update:
update shopBasket
set basket_count=#{basket_count},basket_page=#{basket_page} 
where basket_id=#{basket_id} and product_no=#{product_no}

mapper.select:
SELECT NVL(SUM(item_price * basket_count), 0) sumPrice 
FROM shopBasket b, shopList l
WHERE b.product_no = l.item_no AND b.basket_id = #{basket_id}
